    public void testMovieParser() {
        ArrayList<Movie> movies = null;
        TheMovieDBDataParser p = new TheMovieDBDataParser();

        movies = p.parseMovieJsonStr(popularListJson);
        assertTrue(movies != null);

        Movie m = movies.get(0);
        assertTrue(m.getTitle().equals("The Magnificent Seven"));
    }

    public void testTrailerParser() {
        ArrayList<Trailer> trailers = null;
        TheMovieDBDataParser p = new TheMovieDBDataParser();

        trailers = p.parseTrailerJsonStr(movieTrailerOne);
        assertTrue(trailers != null);
        assertTrue(trailers.size() == 1);

        Trailer t = trailers.get(0);
        assertTrue(t.getId().equals("57f7388ec3a3687c7f00223b"));
        assertTrue(t.getMovieId().equals("399106"));
        assertTrue(t.getName().equals("Piper - Official Trailer"));
        assertTrue(t.getPath().equals("https://www.youtube.com/watch?v=Cn5l7HJaYuA"));

        trailers = p.parseTrailerJsonStr(movieTrailer);
        assertTrue(trailers != null);
        assertTrue(trailers.size() == 9);
    }

    public void testReviewParser() {
        ArrayList<Review> reviews = null;
        TheMovieDBDataParser p = new TheMovieDBDataParser();

        reviews = p.parseReviewJsonStr(movieReviewEmpty);
        assertTrue(reviews != null);
        assertTrue(reviews.size() == 0);

        reviews = p.parseReviewJsonStr(movieReview);
        assertTrue(reviews != null);

        Review r = reviews.get(0);
        assertTrue(r.getMovieId().equals("157336"));
        assertTrue(r.getId().equals("5463856c0e0a267815002598"));
        assertTrue(r.getAuthor().equals("Reno"));
        assertFalse(r.getContent().isEmpty());
    }

    public void testAppendedReviewParser() {
        ArrayList<Review> reviews = null;
        TheMovieDBDataParser p = new TheMovieDBDataParser();

        reviews = p.parseAppendedReviewJsonStr("87", movieTrailerAndReview);
        assertTrue(reviews != null);
        assertTrue(reviews.size() == 2);

        Review firstReview = reviews.get(0);
        Review secondReview = reviews.get(1);
        assertTrue(firstReview.getMovieId().equals("87"));
        assertTrue(secondReview.getMovieId().equals("87"));
        assertTrue(firstReview.getAuthor().equals("Vincent"));
    }

    public void testAppendedTrailerParser() {
        ArrayList<Trailer> trailers = null;
        TheMovieDBDataParser p = new TheMovieDBDataParser();

        trailers = p.parseAppendedTrailerJsonStr("87", movieTrailerAndReview);
        assertTrue(trailers != null);
        assertTrue(trailers.size() == 9);

        Trailer t = trailers.get(0);
        assertTrue(t.getId().equals("551aa15fc3a36823dc001958"));
        assertTrue(t.getMovieId().equals("87"));
        assertTrue(t.getName().equals("Final Cut teaser 2"));
        assertTrue(t.getPath().equals("https://www.youtube.com/watch?v=hadlN0IrXq8"));
    }
